mapper.update:
UPDATE inquiry
SET category=#{category},
    title=#{title},
    content=#{content}
WHERE ino = #{ino}
  AND customer_id = #{customerId}
  AND status = '문의중'

mapper.select:
SELECT *
FROM inquiry
WHERE ino = #{ino}
  AND customer_id = #{customerId}

mapper.delete:
DELETE
FROM inquiry
WHERE ino = #{ino}
  AND customer_id = #{customerId}
  AND status = '문의중'
